import mongoose from "mongoose";

const staffSchema = new mongoose.Schema({
    name: { type: String, required: true },
    email: { type: String, required: true, unique: true },
    password: { type: String, required: true },
    image: { type: String, default: 'data:image/png;base64,' },
    phone: { type: String, required: true },
    role: { type: String, required: true, enum: ['nurse', 'receptionist', 'pharmacist', 'lab_technician', 'admin'] },
    department: { type: String, required: true },
    address: { type: Object, default: { line1: '', line2: '', city: '', state: '', pincode: '' } },
    salary: { type: Number, required: true },
    joiningDate: { type: Date, default: Date.now },
    isActive: { type: Boolean, default: true },
    date: { type: Number, required: true }
}, { minimize: false })

const staffModel = mongoose.models.staff || mongoose.model("staff", staffSchema);
export default staffModel;
